import React, { useState } from 'react'
import { NavLink, Navigate, Route, Routes } from 'react-router-dom';
import './register_style.css'
import { sha3_256 } from 'js-sha3';
import axios from 'axios'

const Register = () => {
    const [email, password, firstname, surname, birthdate, error] = useState('')

    const validate_input = () => {
        if(email.length === 0 || password.length ===0
            || firstname.length === 0 || surname ===0
            || birthdate === '') {
            error = 1
            return false;
        }
        if(!(email.endsWith('@gmail.com') || email.endsWith('@yahoo.com'))) {
            setState({error: 2})
            return false;
        }
        if(password.length < 8) {
            setState({error:3})
            return false;
        }
        setState({error:0})
        return true;
    }

    const submit_register  = (event) =>{
        event.preventDefault();
        let valid = validate_input();
        if(valid) {
            let passkey = sha3_256(password);
            const credentials = {email:email, password:passkey, 
                first_name:firstname, surname:surname,
                birthdate:birthdate};
            console.log(credentials);
            send_post(credentials)
            registered = true

        }
        password = ''
    }
    ev_email = (event) => {
        email = event.target.value
    }
    ev_password = (event) => {
        password = event.target.value
    }
    ev_firstname = (event) => {
        firstname = event.target.value
    }
    ev_surname = (event) => {
        surname = event.target.value
    }
    ev_date = (event) => {
        birthdate = event.target.value
    }
    return (
        <div className= "register">
            <p style={{fontSize: 35,paddingTop: 30}}>Register</p>
            <div className='topbuttonsheader'>
            <label className='current'>Patient</label><NavLink className='topbuttons' to = "../registerdoctor">Doctor</NavLink>
            </div>
            <form onSubmit={submit_register}>

                <label >Email</label>
                <input type = "text" placeholder='type your email' value = {email} onChange = {ev_email}/>

                <label >Password</label>
                <input type = "password" placeholder='type your password' value = {password} onChange = {ev_password}/>

                <label >First Name</label>

                <input type = "text" placeholder='type your first name' value = {firstname} onChange = {ev_firstname}/>

                <label >Surname</label>

                <input type = "text" placeholder='type your surname' value = {surname} onChange = {ev_surname}/>

                <label>Date of Birth</label>


                <input type="date" value={birthdate} onChange={ev_date}/>

                <div className = "buttonc">
                <button>register</button>
                {error === 1 && <p style={{fontSize:17, color: 'red'}}>All fields are required</p>}
                {error === 2 && <p style={{fontSize:17, color: 'red'}}>Invalid email</p>}
                {error === 3 && <p style={{fontSize:17, color: 'red'}}>Password must have minimum 8 characters</p>}

                <p style={{fontSize:17, marginTop:200}}>Already a member? <NavLink to = "/" style={{textDecoration: "none"}}>Login</NavLink> </p>
                {registered && <Navigate replace to ='../registercomplete'/>}
                </div>
            </form>
        </div>
    );
}

export default Register;
