import React, {Component} from 'react'
import './login_style.css'
class Login extends React.Component {
    constructor() {
        super();
        this.state = {
            email: '',
            password: ''
        }
    }

    submit_login = (event) =>{
        event.preventDefault();
        console.log(this.state.email + " " + this.state.password);
        this.setState({email : ''});
        this.setState({password: ''});

    }
    email_change = (event) => {
        this.setState({email : event.target.value});
    }
    password_change = (event) => {
        this.setState({password : event.target.value});
    } 
    render() {
    return (
        <div class = "login">
            <p style={{fontSize: 35,paddingTop: 30}}>Login</p>
            <form onSubmit={this.submit_login}>
                <label style={{marginTop: 30, marginBottom:5} }>Email</label>

                <input type = "text" value = {this.state.email} onChange = {this.email_change}/>

                <label style={{marginTop: 20, marginBottom:5}}>Password</label>

                <input type = "password" onChange={this.password_change}/> 
                <div class = "buttonc">
                <button>log in</button>
                </div>
            </form>
            <div style={{margin:5, marginLeft:195}}>
                <a href = "forgotpasspage" style={{textDecoration:"none", color: "black", fontSize:17}}>forgot password? </a>
            </div>
            <p style={{fontSize:17, marginTop:150}}>Not a member? <a href = "registerpage" style={{textDecoration: "none"}}>Register</a> </p>
        </div>
    );
    }
}

export default Login;
